<div class="orders-select">	<ul>		<li class="left <?php print(($this->session->userdata('group')=="yes" || $this->session->userdata('group') == null) ? '' : 'active'); ?>"><a  href="#" onclick="document.order_group.submit();">Одним списком</a></li>		<li class="right <?php print(($this->session->userdata('group')=="yes" || $this->session->userdata('group') == null) ? 'active' : ''); ?>"><a href="#" onclick="document.order_group.submit();">По категориям</a></li>	</ul>							<form method="post" action="" name="order_group">							<input type="hidden" name="group" value="<?php print(($this->session->userdata('group')=="yes" || $this->session->userdata('group') == null) ? 'no' : 'yes'); ?>" />						</form></div><article><div class="orders"><table class="order-list">	<thead>		<tr id="ct_sort">			<th class="name">Продукт</th>			<th>Кол.</th>			<th>Цена</th>			<th>Итого</th>		</tr>	</thead>	<tbody>		<?php if($this->Cart_model->list_categories()) {  ?>		<?php $i = 0; ?> 			<?php foreach($this->Cart_model->list_categories() as $one): ?>					<tr class="head" id="<?php echo $one->guid;?>">					<td colspan="4"><?php echo $this->Cart_model->list_cat($one->guid, TRUE); ?></td>				</tr>				<?php foreach($this->Cart_model->list_orders($one->guid) as $two): ?>					<?php $i++; ?>					<?php $evro = $two->evro; ?>					<tr id="line<?php echo $two->orderid;?>" <?php if($i%2 == 0) echo 'class="block '.$one->guid.' even"';?> class="block <?php echo $one->guid;?>">						<td class="name"><a class="remove-row" order_id="<?php echo $two->orderid;?>" href="#" title="Удалить строку"><img alt="" src="/img/btn-close.png" /></a><?php echo $two->name;?></td>						<td><?php echo $two->number;?></td>						<td><?php $price=number_format($two->price*$evro,2); echo $price;?></td>						<td><span id="l_p<?php echo $two->orderid;?>"><?php echo number_format($two->price_rur,2); ?></span></td>                                					</tr>				<?php endforeach; ?>			<?php endforeach; ?>				<tr class="allsumm">			<td colspan="4">Сумма заказа: <span><?php echo number_format($cat['allsumm'],2);?> руб.</span></td>		</tr>		<?php } else { ?>		<tr>			<td colspan="4">Нет заказанных товаров</td>		</tr>		<?php } ?>	</tbody></table></div><?php if($this->Cart_model->list_categories()):  ?><div class="orders"><div class="orders-bottom">		<textarea onchange="comment();" onblur="if(!value)value=defaultValue" onfocus="if(value==defaultValue)value=''" class="comment_text" style="margin: 10px 0;" name="comment_text" cols="30" rows="3">Добавьте комментарий к заказу...</TEXTAREA><br>		<div class="table_buttons clearfix">	<form class="cleartable button" method="post" action="/mobile/cart/">		<input type="submit" value="Очистить"/>		<input type="hidden" name="clear" value="all" />	</form>	<form class="submittable button" method="post" action="/mobile/cart/">		<input onclick="comment();" type="submit" value="Отправить заказ"/>		<input type="hidden" name="order" value="all" />		<input type="hidden" class="comment" name="comment" value="" />	</form></div></div></div><?php endif; ?></article>